import { initialState } from "../Modal/Modal";
console.log("inReducer", initialState);

const countReducer = (state = initialState, action) => {
   switch (action.type) {
      case "increment":
         return { ...state, count: Number(state[0].count + 1) };
      case "decrement":
         return { ...state, count: Number(state[0].count - 1) };
      default:
         return state;
   }
};

// const rootReducer = combineReducers(countReducer);

export default countReducer;
