import React from "react";
// import { useReducer } from "react";

const Modal = ({ open, onClose, CartData }) => {
   const MODAL_styles = {
      position: "fixed",
      top: "30%",
      left: "30%",
      width: "50%",
      tranform: "translate(-50%, -50%)",
      backgroundColor: "#fff",
      padding: "30px",
      zIndex: 1000,
      borderRadius: "10px",
      textAlign: "center",
   };

   const overlay_styles = {
      position: "fixed",
      top: "0",
      left: "0",
      right: "0",
      bottom: "0",
      backgroundColor: "rgba(0, 0, 0, .7)",
      zIndex: 1000,
   };
   // console.log("this cart Data", CartData);
   if (!open) return null;

   initialState = CartData;

   // function reducer(state, action) {
   //    switch (action.type) {
   //       case "increment":
   //          return { ...state, count: state.count + 1 };
   //       case "decrement":
   //          return { ...state, count: state.count - 1 };
   //       default:
   //          throw state;
   //    }
   // }
   // const [state, dispatch] = useReducer(reducer, initialState);

   return (
      <div style={overlay_styles}>
         <div style={MODAL_styles}>
            {CartData.length > 0 ? (
               CartData.map((item) => (
                  <div className="container d-flex flex-row">
                     <div className="container text-start">
                        <div className="d-flex flex-column">
                           <strong>{item.name}</strong>
                           <i>{item.desc}</i>
                           <div className="price d-flex justify-content-between">
                              <span>${item.price}</span>
                              <span className="text-white bg-secondary ps-2 pe-2 rounded-2">
                                 {item.count}
                              </span>
                           </div>
                        </div>
                     </div>
                     <div className="container d-block">
                        <div>${item.price * item.count}</div>
                        <button
                           className="btn me-2"
                           // onClick={() => dispatch({ type: "decrement" })}
                        >
                           -
                        </button>
                        <button className="btn" onClick={props.incCounter}>
                           +
                        </button>
                     </div>
                     <hr />
                  </div>
               ))
            ) : (
               <h1>NO Items added in Cart</h1>
            )}
            <button className="btn bg-primary mt-5 mb-2 " onClick={onClose}>
               Checkout
            </button>
         </div>
      </div>
   );
};

let initialState;

// export default Modal;
export { initialState };


const mapStateToProps = (state) => {
   return {
      count: state.count,
   };
};

const mapDispatchToProps = (dispatch) => {
   return {
      incCounter: () => dispatch(incCounter()),
      decCounter: () => dispatch(decCounter()),
   };
};

export default connect(mapStateToProps, mapDispatchToProps)(Counter);
