import React, { useReducer } from "react";

const Modal = ({ open, onClose, CartData }) => {
   const MODAL_styles = {
      position: "fixed",
      top: "30%",
      left: "30%",
      width: "50%",
      tranform: "translate(-50%, -50%)",
      backgroundColor: "#fff",
      padding: "50px",
      zIndex: 1000,
      borderRadius: "10px",
      textAlign: "center",
   };

   const overlay_styles = {
      position: "fixed",
      top: "0",
      left: "0",
      right: "0",
      bottom: "0",
      backgroundColor: "rgba(0, 0, 0, .7)",
      zIndex: 1000,
   };
   console.log("this cart Data", CartData);
   if (!open) return null;

   const initialState = {
      numOfCakes: 20,
      numOfIcecream: 10,
   };

   function reducer(state, action) {
      switch (action.type) {
         case "BUY_CAKE":
            return { ...state, numOfCakes: state.numOfCakes - 1 };
         case "BUY_ICECREAM":
            return { ...state, numOfIcecream: state.numOfIcecream - 1 };
         default:
            throw new Error();
      }
   }
   const [state, dispatch] = useReducer(reducer, initialState);

   return (
      <div style={overlay_styles}>
         <div style={MODAL_styles}>
            {CartData.length > 0 ? (
               CartData.map((item) => (
                  <div className="container d-flex flex-row">
                     <div className="container">
                        <div className="d-flex flex-column">
                           <strong>{item.name}</strong>
                           <i>{item.desc}</i>
                           <div className="price">${item.price}</div>
                        </div>
                     </div>
                     <div className="container d-block">
                        <div>$34</div>
                        <button className="btn" onClick={() => dispatch({ type: "BUY_CAKE" })}>
                           -
                        </button>
                        <button
                           onClick={() => dispatch({ type: "BUY_ICECREAM" })}
                        >
                           +
                        </button>
                     </div>
                     <hr />
                  </div>
               ))
            ) : (
               <h1>NO Items added in Cart</h1>
            )}
            <button className="btn bg-primary text-center" onClick={onClose}>
               Checkout
            </button>
         </div>
      </div>
   );
};

export default Modal;
