// import { combineReducers } from "redux";
// const initialState = {
//    count: 10,
// };

let foodcart = localStorage.getItem("cartData");
// console.log("inReducer", initialState);
if (typeof foodcart === "undefined" || foodcart === "" || foodcart === null) {
   foodcart = { cartItem: [], totalPrice: 0, totalQty: 0 };
} else {
   foodcart = JSON.parse(foodcart);
}

const countReducer = (state = foodcart, action) => {

   const {shoppingCart,totalPrice,totalQty}=state;//Destructuring 
   switch (action.type) {
      case "increment":
         return { ...state, count: state.count + 1 };
      case "decrement":
         return { ...state, count: state.count - 1 };
      default:
         return state;
   }
};

// const rootReducer = combineReducers(countReducer);

export default countReducer;
