// import { combineReducers } from "redux";
// const initialState = {
//    count: 10,
// };

let foodcart = localStorage.getItem("cartData");
// console.log("inReducer", initialState);
if (typeof foodcart === "undefined" || foodcart === "" || foodcart === null) {
   foodcart = { cartItem: [], totalPrice: 0, totalQty: 0 };
} else {
   foodcart = JSON.parse(foodcart);
}

const countReducer = (state = foodcart, action) => {
   const itemData = action.payload;
   const { CartItems, totalPrice, totalQty } = state;
   let product;
   let index;
   let updatedPrice;
   let updatedQty;
   let updatedCart;
   switch (action.type) {
      case "addToCart":
         console.log("in AddTOCart", action);
         const check = CartItems.find(
            (cartProduct) => cartProduct.id === itemData.id
         );
         if (check) {
            return state;
         } else {
            product = itemData.itemDetails;
            updatedQty = totalQty + 1;
            updatedPrice = totalPrice + product.price;
            updatedCart = {
               CartItems: [product, ...CartItems],
               totalPrice: updatedPrice,
               totalQty: updatedQty,
            };
            localStorage.setItem("foodcart", JSON.stringify(updatedCart));
            return updatedCart;
         }
         break;
      case "increment":
         return { ...state, count: state.count + 1 };
      case "decrement":
         return { ...state, count: state.count - 1 };
      default:
         return state;
   }
};

// const rootReducer = combineReducers(countReducer);

export default countReducer;
