import React from "react";

const Modal = ({ open, onClose }) => {
   const MODAL_styles = {
      position: "fixed",
      top: "50%",
      left: "50%",
      tranform: "translate(-50%, -50%)",
      backgroundColor: "#fff",
      padding: "50px",
      zIndex: 1000,
   };

   const overlay_styles = {
    position: 'fixed',
    top :'0',
    left : '0',
    right : '0',
    bottom : '0',
    backgorundColor : 'rgba(0, 0, 0, 0.7'
   }

   if (!open) return null;
   return (
      <div style={MODAL_styles}>
         <button onClick={onClose}>closeModal</button>
      </div>
   );
};

export default Modal;
