import "./App.css";
import Modal from "./components/Modal/Modal";
import Items from "./components/Items/Items";
import NavBar from "./components/Navbar/NavBar";
import React, { useState } from "react";
function App() {
   const food = [
      {
         id: 1,
         name: "Sushi",
         desc: "Finest fish and veggies",
         price: 22.99,
      },
      {
         id: 2,
         name: "Schnitzel",
         desc: "A german speciality!",
         price: 16.5,
      },
      {
         id: 3,
         name: "Barbecue Burger",
         desc: "American, raw, mealy",
         price: 12.99,
      },
      {
         id: 4,
         name: "Green Bowl",
         desc: "Healthy ..and green..",
         price: 18.99,
      },
   ];
   const [Count, setCount] = useState(0);
   const [open, setOpen] = useState(false);
   const [CartData, setCartData] = useState([]);
   const addToCart = (item) => {
      // console.log(`In Items ${item.count} and id is ${item.itemId}`);
      setCount((cnt) => Number(cnt) + Number(item.count));

      for (const element of food) {
         console.log(element);
      }

      console.log(CartData);
   };

   //  const isOpen = (value) => {
   //     console.log(open);
   //     setOpen(value);
   //  };

   return (
      <div className="App">
         <NavBar count={Count} onOpen={() => setOpen(true)} />
         <Items food={food} onSelect={addToCart} />
         <Modal
            open={open}
            onClose={() => setOpen(false)}
            CartData={CartData}
         />
      </div>
   );
}

export default App;
