import "./App.css";
import Modal from "./components/Modal/Modal";
import Items from "./components/Items/Items";
import NavBar from "./components/Navbar/NavBar";
import React, { useState } from "react";
import { Provider } from "react-redux";
import store from "./components/store";

function App() {
   const food = [
      {
         id: 1,
         name: "Sushi",
         desc: "Finest fish and veggies",
         price: 22.99,
      },
      {
         id: 2,
         name: "Schnitzel",
         desc: "A german speciality!",
         price: 16.5,
      },
      {
         id: 3,
         name: "Barbecue Burger",
         desc: "American, raw, mealy",
         price: 12.99,
      },
      {
         id: 4,
         name: "Green Bowl",
         desc: "Healthy ..and green..",
         price: 18.99,
      },
   ];
   const [Count, setCount] = useState(0);
   const [open, setOpen] = useState(false);
   const [CartData, setCartData] = useState([]);
   const addToCart = (item) => {
      console.log(`In Items ${item.count} and id is ${item.itemId}`);
      setCount((cnt) => Number(cnt) + Number(item.count));

      for (const ele of food) {
         if (item.itemId === ele.id) {
            setCartData([
               ...CartData,
               {
                  id: ele.id,
                  name: ele.name,
                  desc: ele.desc,
                  price: ele.price,
                  count: item.count,
               },
            ]);
            break;
         }
      }
   };

   return (
      <Provider store={store}>
         <div className="App">
            <NavBar count={Count} onOpen={() => setOpen(true)} />
            <Items food={food} onSelect={addToCart} />
            <Modal
               open={open}
               onClose={() => setOpen(false)}
               CartData={CartData}
            />
         </div>
      </Provider>
   );
}

export default App;
