import React from "react";
import { useReducer } from "react";

const Counter = () => {
   const initialState = {
      numOfCakes: 20,
      numOfIcecream: 10,
   };

   function reducer(state, action) {
      switch (action.type) {
         case "BUY_CAKE":
            return { ...state, numOfCakes: state.numOfCakes - 1 };
         case "BUY_ICECREAM":
            return { ...state, numOfIcecream: state.numOfIcecream - 1 };         default:
            throw new Error();
      }
   }
   const [state, dispatch] = useReducer(reducer, initialState);
   return (
      <>
         <h1>Cake_Count: {state.numOfCakes}</h1>
         <h1>Icecream_Count: {state.numOfIcecream}</h1>

         <button onClick={() => dispatch({ type: "BUY_CAKE" })}>
            BUY_CAKE
         </button>
         <button onClick={() => dispatch({ type: "BUY_ICECREAM" })}>
            BUY_ICECREAM
         </button>
      </>
   );
};

export default Counter;
